import {React , useState , useEffect} from "react";
import { Col, Container, Row } from "react-bootstrap";
import { FaArrowRight} from 'react-icons/fa';
import header_1 from '../assets/header_1.png'

const Header = () => {
    const [typingLoob , setTypingLoob ] = useState(0) 
    const [isDeleting , setIsDeleting] = (false)
    const toRotate = ["Front-End Developer" , "Back-End Designer" , "Full-Stack Developer" ];
    const [typeWriter , setTypeWriter] = useState('')
    const time = 2000;
    const [delta , setDelta ] = useState(300 - Math.random() * 100)
    useEffect (() => {
        let ticker = setInterval(() => {
            tick()
        } , delta)
        return (
            clearInterval(ticker)
        )
    } , [typeWriter] )
    const tick = ( ) => {
        let i = typingLoob % toRotate.length
        let fullText = toRotate[i]
        let updatedText = isDeleting ? fullText.substring(0 , typeWriter.length - 1) : fullText.substring(0 , typeWriter.length + 1)
        setTypeWriter(updatedText)
        if (isDeleting) {
            setDelta(prevDelta => prevDelta/2)
        }
        if (!isDeleting && updatedText === fullText) {
            setIsDeleting(true)
            setDelta(prevDelta => prevDelta/2)
        }
    }
    return (
        <section className="header section" id="home">
            <Container>
                <Row className="align-items-center">
                    <Col md={6}>
                        <div className="text_box">
                            <h5 className="subtitle text-capitalize text-white mb-3">Welcome to my portfolio</h5>
                            <h1 className="title fw-bold mb-4 text-white">
                                Hi I'm Wafaa Hegazy 
                                <span className="wrap">Web Developer</span>
                            </h1>
                            <p className="det text-white-50">
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit,  Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."
                            </p>
                            <button className="butn primary_border_butn mt-5" onClick={() => {console.log('Contact')}}>
                                Contact Me <FaArrowRight className="ms-2"/>
                            </button>
                        </div>
                    </Col>
                    <Col md={6}>
                        <img src={header_1} alt="Header img"/>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}

export default Header ;
